DROP TABLE IF EXISTS `admin`;CREATE TABLE `admin` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(30) DEFAULT NULL COMMENT '用户名',  `password` varchar(40) DEFAULT NULL COMMENT '密码',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='管理员表';DROP TABLE IF EXISTS `auth`;CREATE TABLE `auth` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `user_id` int(11) DEFAULT NULL,  `secret` varchar(64) DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  PRIMARY KEY (`id`),  KEY `user_id` (`user_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `category`;CREATE TABLE `category` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `title` varchar(30) DEFAULT NULL,  `parent` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `content`;CREATE TABLE `content` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `title` varchar(80) DEFAULT NULL,  `pic` varchar(150) DEFAULT NULL,  `cat_id` int(11) DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  `text` text DEFAULT NULL,  `is_index` boolean DEFAULT NULL,    `is_top` boolean DEFAULT NULL,    PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `company`;CREATE TABLE `company` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `logo` varchar(125) DEFAULT NULL,  `name` varchar(20) DEFAULT NULL,  `url` varchar(50) DEFAULT NULL,  `status` tinyint(1) DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;LOCK TABLES `company` WRITE;INSERT INTO `company` (`id`, `logo`, `name`, `url`, `status`, `addtime`) VALUES(1, '/static/upload/1361564251.jpg', '盛世互动', '', 1, '2013-02-19 18:08:46'),(2, '/static/upload/1361564262.jpg', '魔方', '', 1, '2013-02-19 18:08:46');UNLOCK TABLES;DROP TABLE IF EXISTS `game`;CREATE TABLE `game` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(20) DEFAULT NULL,  `company_id` int(11) DEFAULT NULL,  `url` varchar(50) DEFAULT NULL,  `status` tinyint(1) DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  PRIMARY KEY (`id`),  KEY `company_id` (`company_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;INSERT INTO `game` (`id`, `name`, `company_id`, `url`, `status`, `addtime`) VALUES(1, '兵马俑', 1, '', 1, '2013-02-23 15:30:16');DROP TABLE IF EXISTS `iproduct`;CREATE TABLE `iproduct` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `orderno` varchar(50) DEFAULT NULL,  `user_id` int(11) DEFAULT NULL,  `isActive` tinyint(1) DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  PRIMARY KEY (`id`),  KEY `user_id` (`user_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `order`;CREATE TABLE `order` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `orderno` varchar(15) DEFAULT NULL,  `pro_id` int(11) DEFAULT NULL,  `account` varchar(50) DEFAULT NULL,  `email` varchar(50) DEFAULT NULL,  `idcard` varchar(20) DEFAULT NULL,  `name` varchar(5) DEFAULT NULL,  `sex` tinyint(1) DEFAULT NULL,  `birth` date DEFAULT NULL,  `mobile` varchar(15) DEFAULT NULL,  `phone` varchar(20) DEFAULT NULL,  `address` varchar(150) DEFAULT NULL,  `zip` varchar(6) DEFAULT NULL,  `express` varchar(10) DEFAULT NULL,  `expressno` varchar(30) DEFAULT NULL,  `status` tinyint(1) DEFAULT NULL,  `price` float DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  PRIMARY KEY (`id`),  KEY `pro_id` (`pro_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `product`;CREATE TABLE `product` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `game_id` int(11) DEFAULT NULL,  `level` int(11) DEFAULT NULL,  `peichang` float DEFAULT NULL,  `price` float DEFAULT NULL,  `promo` float DEFAULT NULL,    PRIMARY KEY (`id`),  KEY `game_id` (`game_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;INSERT INTO `product` (`id`, `game_id`, `level`, `peichang`, `price`, `promo`) VALUES(1, 1, 1, 100000, 120, 0.01),(2, 1, 2, 200000, 180, 0.02);DROP TABLE IF EXISTS `user`;CREATE TABLE `user` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `username` varchar(30) DEFAULT NULL,  `password` varchar(40) DEFAULT NULL,  `name` varchar(5) DEFAULT NULL,  `email` varchar(100) DEFAULT NULL,  `addtime` datetime DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `userinfo`;CREATE TABLE `userinfo` (  `id` int(11) NOT NULL,    `birth` date DEFAULT NULL,  `sex` tinyint(1) DEFAULT NULL,  `mobile` varchar(15) DEFAULT NULL,  `address` varchar(254) DEFAULT NULL,  `phone` varchar(20) DEFAULT NULL,  `zip` varchar(6) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;